// src/RailwayMap.jsx
import React from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import "leaflet-defaulticon-compatibility";
import "leaflet-defaulticon-compatibility/dist/leaflet-defaulticon-compatibility.css";

// Example stations with lat/lng and names
const stations = [
  { name: "Station A", lat: 20.2961, lng: 85.8245 },
  { name: "Station B", lat: 20.2675, lng: 85.8436 },
  { name: "Station C", lat: 20.2556, lng: 85.7973 }
];

const RailwayMap = () => {
  return (
    <div style={{ height: "90vh", width: "100%" }}>
      <MapContainer center={[20.2961, 85.8245]} zoom={12} scrollWheelZoom={true} style={{ height: "100%", width: "100%" }}>
        <TileLayer
          attribution='Â© <a href="https://osm.org/copyright">OpenStreetMap</a>'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />

        {stations.map((station, index) => (
          <Marker key={index} position={[station.lat, station.lng]}>
            <Popup>
              <strong>{station.name}</strong><br />
              <a href="#">Upload/View Documents</a>
              {/* We'll link this to a form/modal in the next step */}
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
};

export default RailwayMap;
